mapper.insert:
/*boardNumber로 FK받아서 연결해준다.*/
     INSERT INTO TBL_FILE (FILE_NUMBER, FILE_NAME, FILE_UPLOAD_PATH, FILE_UUID, FILE_SIZE, FILE_IMAGE_CHECK, BOARD_NUMBER)
     VALUES(SEQ_FILE.NEXTVAL, #{fileName}, #{fileUploadPath}, #{fileUuid}, #{fileSize}, #{fileImageCheck}, #{boardNumber})

mapper.select:
/*boardNumber로 조회하면 해당 파일VO들을 찾아올수있다.*/
       SELECT FILE_NUMBER, FILE_NAME, FILE_UPLOAD_PATH, FILE_UUID, FILE_SIZE, FILE_IMAGE_CHECK, BOARD_NUMBER
       FROM TBL_FILE
       WHERE BOARD_NUMBER = #{boardNumber}

mapper.delete:
/*해당 boardNumber지우면 삭제*/
       DELETE FROM TBL_FILE
       WHERE BOARD_NUMBER = #{boardNumber}
